import React, { useEffect, useState } from 'react';
import { View, Image, ImageBackground, AsyncStorage, NativeModules, Dimensions, StyleSheet, TouchableOpacity, Text } from 'react-native';
import { useIsFocused } from '@react-navigation/native';
import { Ionicons, FontAwesome, MaterialCommunityIcons, FontAwesome5 } from '@expo/vector-icons';
import { createStackNavigator } from '@react-navigation/stack';
import { SHCss } from "../Common/SHCss";
import { GetApis } from '../Common/CallApi';
import { ScrollView } from 'react-native';
import { TabView, SceneMap } from 'react-native-tab-view';
import Theme from '../constants/Theme';

const initialLayout = {
    height: 0,
    width: Dimensions.get('window').width,
};
const Stack = createStackNavigator();

const ProfileTabComponent = (props) => {
    const isFocused = useIsFocused();
    var [ObjLogin, setObjLogin] = useState('');
    var [index, setIndex] = useState(0);
    const initialLayout = { width: Dimensions.get('window').width };
    const getObj = async () => {
        let objStore = await AsyncStorage.getItem('userLogin');
        objStore = JSON.parse(objStore);
        var res = await GetApis('KPI', 'GetInfoUserByUserName', { username: objStore.UserName, }, 3000);
        if (res.ResponseStatus == "OK") {
            if (res.ResponseData != null) {
                let data = res.ResponseData.data;
                data["ProfilePicture"] = objStore.ProfilePicture;
                data["ProfilePicturePath"] = objStore.ProfilePicturePath;
                setObjLogin(data);
            }
        }
    }
    useEffect(() => {
        if (isFocused) {
            getObj();
        }
    }, [props, isFocused])
    return (
        <View style={styles.container}>
            <ImageBackground style={[styles.image]}>
                <View style={styles.vAvatar}>
                    <Image
                        style={styles.avatar}
                        source={{ uri: ObjLogin.ProfilePicture == null ? ObjLogin.ProfilePicturePath : `https://acc.sunhouse.com.vn/ProfilePictures/${ObjLogin.ProfilePicture}` }}
                        resizeMode='contain'
                    />
                </View>
                <Text style={styles.text}>{ObjLogin.FullName}</Text>
                <Text style={styles.textInfo}>{ObjLogin.job_titleName}</Text>
                <Text style={styles.textInfo}>{ObjLogin.CostCenterName}</Text>
            </ImageBackground>
            <View style={{ flex: 2 }}>
                <FirstRoute ObjLogin={ObjLogin} />
            </View>
        </View>
    )
}

const ProfileTab = (props) => {
    //const { signOut } = React.useContext(AuthContext);
    return (
        <Stack.Navigator initialRouteName="ProfileTabComponent">
            <Stack.Screen
                name="ProfileTabComponent"
                component={ProfileTabComponent}
                options={{
                    headerLeft: () => (
                        <TouchableOpacity style={{ marginHorizontal: 10 }} onPress={() => props.navigation.openDrawer()}>
                            <Ionicons name="ios-menu" size={30} color="white" />
                        </TouchableOpacity>
                    ),
                    title: 'Tài khoản',
                    headerStyle: {
                        backgroundColor: Theme.COLORS.GREEN_PORTAL,
                        minHeight:70
                    },
                    headerTitleAlign: 'center',
                    headerTintColor: '#fff',
                    headerTitleStyle: {
                        fontWeight: 'bold',
                    },                    
                }} />
        </Stack.Navigator>
    );
}

const FirstRoute = (props) => {
    return (
        <ScrollView style={{ paddingTop: 4 }}>
            <View style={styles.row}>
                <FontAwesome style={styles.icon} name="birthday-cake" size={20}/>
                <View style={styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>Ngày sinh</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.strgeb_ldat1}</Text>
                </View>
            </View>
            <View style={styles.row}>
                <FontAwesome style={styles.icon} name="phone" size={24}/>
                <View style = {styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>Số điện thoại</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.PhoneNumber}</Text>
                </View>
            </View>
            <View style={styles.row}>
                <MaterialCommunityIcons style={styles.iconMail} name="email" size={23}/>
                <View style={styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>Email</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.Email}</Text>
                </View>
            </View>
            <View style={styles.row}>
                <FontAwesome5 style={styles.icon} name="facebook-square" size={23}/>
                <View style={styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>Facebook</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.FacebookAddress}</Text>
                </View>
            </View>
            <View style={styles.row}>
                <MaterialCommunityIcons style={styles.icon} name="home-city" size={23}/>
                <View style={styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>Địa chỉ</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.Address}</Text>
                </View>
            </View>
            <View style={styles.row}>
                <FontAwesome style={styles.icon} name="user" size={23}/>
                <View style={styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>UserName</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.UserName}</Text>
                </View>
            </View>
            <View style={styles.row}>
                <Ionicons style={styles.icon} name="md-today" size={23}/>
                <View style={styles.textDisplayInfo}>
                    <Text style={styles.textLableInfo}>Ngày vào</Text>
                    <Text style={styles.textInfo}>{props.ObjLogin.strStartDate1} {getDateNumberJoin(props.ObjLogin.strStartDate1)}</Text>
                </View>
            </View>
        </ScrollView>
    );
}

//lấy ra thời gian kể từ khi vào công ty
const getDateNumberJoin = (strStartDate1) => {
    if(strStartDate1){
        var arrDate = strStartDate1.split("-");
        var strDateFormat = arrDate[2] + "-" + arrDate[1] + "-" + arrDate[0];
        var date = new Date(strDateFormat);
        var dateNow = new Date();
        var dateNumber = (dateNow-date)/(24 * 60 * 60 * 1000);
        var strTime = "";
        if(dateNumber < 60){
            strTime = dateNumber + " ngày"; 
        }
        else if(dateNumber < 365){
            strTime = parseInt(dateNumber/30) + " tháng" ; 
        }
        else if(dateNumber > 365){
            strTime = parseInt(dateNumber/365) + " năm " + parseInt(dateNumber - ((parseInt(dateNumber/365))*365)) + " ngày";
        }
    }
    
    return strTime;
}
const styles = StyleSheet.create({
    tab: {
        backgroundColor: 'red',
        color: 'black'
    },
    container: {
        flex: 1,
        flexDirection: "column",
    },
    image: {
        flex: 1,
        minHeight: 100,
        //height: 100,
        flexDirection: 'column',
        resizeMode: "cover",
        alignItems: 'center',
        justifyContent: 'center',
        borderBottomColor: '#e5e0e0',
        borderBottomWidth: 2
        // backgroundColor: '#5E73E5',
        // borderBottomLeftRadius: 20,
        // borderBottomRightRadius: 20,
    },
    text: {
        color: "black",
        fontSize: 25,
        fontWeight: "bold",
