import React, { useEffect, useState } from 'react';
import { View, StyleSheet, TouchableOpacity, Alert, Text, Dimensions, Platform } from 'react-native';
import { useIsFocused } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import DateTimePickerModal from "react-native-modal-datetime-picker";
import { ScrollView } from "react-native-gesture-handler";
import { theme } from 'galio-framework';
import moment from 'moment';
import Dialog from "react-native-dialog";

import { SHCss } from "../Common/SHCss";
import SHLabel from '../components/controls/SHLabel';
import SHInputText from '../components/controls/SHInputText';
import SHTextArea from '../components/controls/SHTextArea';
import SHRNPicker from '../components/controls/SHRNPicker';
import SHModelPicker from '../components/controls/SHModelPicker';
import Theme from '../constants/Theme';

import AsyncStorage from "@react-native-async-storage/async-storage";
import { GetApis, PostApis } from '../Common/CallApi';


const Stack = createStackNavigator();

const AddTaskComponent = ({ navigation }, props) => {
    const [isDatePickerVisible, setDatePickerVisibility] = useState(false);
    const isFocused = useIsFocused();
    const today = new Date(new Date().setHours(0, 0, 0, 0));
    const strtoday = moment(today).format("DD-MM-YYYY");
    var [visibleMsgSuccess, setVisibleMsgSuccess] = useState(false);
    var [taskName, setTaskName] = useState('');
    var [taskRequirement, setTaskRequirement] = useState('');
    var [fdate, setFDate] = useState(today);
    var [tdate, setTDate] = useState(today);
    var [typeDate, setTypeDate] = useState('fdate');
    var [strFDate, setstrFDate] = useState(strtoday);
    var [strTDate, setstrTDate] = useState(strtoday);
    var [performUser, setPerformUser] = useState(null);
    var [performUserTxt, setPerformUserTxt] = useState(null);
    var [managerUser, setManagerUser] = useState(null);
    var [managerUserTxt, setManagerUserTxt] = useState(null);
    var [followUser, setFollowUser] = useState(null);
    var [followUserTxt, setFollowUserTxt] = useState(null);
    var [lstEmp, setLstEmp] = useState([]);
    var [lstInterEmp, setLstInterEmp] = useState([]);

    //clear form
    const onClearForm = () => {
        setTaskName(null);
        setTaskRequirement(null);
        setPerformUser(null);
        setPerformUserTxt(null);
        setManagerUser(null);
        setManagerUserTxt(null);
        setFollowUser(null);
        setFollowUserTxt(null);
        setFDate(today);
        setTDate(today);
        setstrFDate(moment(today).format("DD-MM-YYYY").toString());
        setstrTDate(moment(today).format("DD-MM-YYYY").toString());
    }

    /**
     * Sự kiện show model chọn mgày tháng theo loại
     * @param {*} typeDate Từ ngày hay đến ngày hay...
     */
    const showDatePicker = (typeDate) => {
        setDatePickerVisibility(true);
        setTypeDate(typeDate);
    };

    const hideDatePicker = () => {
        setDatePickerVisibility(false);
    };

    /**
     * xử lý sự kiện chọn ngày
     * @param {*} date 
     */
    const handleConfirm = (date) => {
        hideDatePicker();
        if (typeDate == 'fdate') {
            setstrFDate(moment(date).format("DD-MM-YYYY").toString());
            setFDate(date);
        } else if (typeDate == 'tdate') {
            setstrTDate(moment(date).format("DD-MM-YYYY").toString());
            setTDate(date);
        }
    };

    //lưu việc
    const onSaveTask = async () => {
        //#region validate
        if (taskName == "" || taskName == null) {
            Alert.alert(`Lỗi!`, 'Bạn chưa nhập tên công việc.', [{ text: "Đóng" }],);
            return false;
        }
        if (taskName.length > 500) {
            Alert.alert('Lỗi!', 'Tên công việc không quá 500 kí tự.', [{ text: "Đóng" }],);
            return false;
        }
        if (taskRequirement == "" || taskRequirement == null) {
            Alert.alert('Lỗi!', 'Bạn chưa nhập yêu cầu.', [{ text: "Đóng" }],);
            return false;
        }

        if (fdate === null || fdate === undefined) {
            Alert.alert('Lỗi!', 'Bạn chưa nhập từ ngày.', [{ text: "Đóng" }],);
            return false;
        }
        if (fdate < today) {
            Alert.alert('Lỗi!', 'Từ ngày không được nhỏ hơn ngày hiện tại.', [{ text: "Đóng" }],);
            return false;
        }

        if (tdate === null || tdate === undefined) {
            Alert.alert('Lỗi!', 'Bạn chưa nhập đến ngày.', [{ text: "Đóng" }],);
            return false;
        }
        if (tdate < today) {
            Alert.alert('Lỗi!', 'Đến ngày không được nhỏ hơn ngày hiện tại.', [{ text: "Đóng" }],);
            return false;
        }
        if (fdate > tdate) {
            Alert.alert('Lỗi!', 'Từ ngày phải nhỏ hơn đến ngày.', [{ text: "Đóng" }],);
            return false;
        }
        if (performUser === null || performUser === undefined || performUser == '') {
            Alert.alert('Lỗi!', 'Bạn chưa chọn người thực hiện.', [{ text: "Đóng" }],);
            return false;
        }

        if (managerUser === null || managerUser === undefined || managerUser == '') {
            Alert.alert('Lỗi!', 'Bạn chưa chọn người đánh giá.', [{ text: "Đóng" }],);
            return false;
        }
        //#endregion 
        let userLogin = await AsyncStorage.getItem("userLogin");

        userLogin = JSON.parse(userLogin);

        try {
            let userName = userLogin.UserName;
            let obj = {
                taskName: taskName,
                taskRequirement: taskRequirement,
                performUser: performUser,
                managerUser: managerUser,
                followUser: followUser == null ? '' : followUser,
                fdate: typeof fdate == 'object' ? moment(fdate).format("DD-MM-YYYY") : fdate,
                tdate: typeof tdate == 'object' ? moment(tdate).format("DD-MM-YYYY") : tdate,
                loginUser: userName
            }

            var res = await PostApis('KPI', `CreateTaskAssignment1`, obj, 3000);
            if (res.ResponseStatus == "OK") {
                if (res.ResponseData == null) {
                    Alert.alert('Lỗi!', res.ResponseMessenger, [{ text: "Đóng" }],);
                    return false
                }
                if (res.ResponseData != "success") {
                    Alert.alert('Lỗi!', res.ResponseData, [{ text: "Đóng" }],);
                    return false
                }
                setVisibleMsgSuccess(true);
                return false
            }
        }
        catch (error) {
            console.log(error);
        }

    }

    const closeAddTask = (navigation) => {
        Alert.alert(
            'Xác nhận',
            'Bạn có chắc muốn hủy bỏ?',
            [{
                text: 'Đồng ý',
                style: { color: theme.COLORS.ERROR },
                onPress: () => {
                    navigation.navigate("HomeTabBar");
                },
            },
            {
                text: 'Không',
                onPress: () => {
                    return null;
                },
            },
            ],
            { cancelable: false },
        );
    }

    useEffect((props) => {
        /**
         * Lấy ds nhân viên
         * DUOCNC 20210712
         */
        const getListUser = async (props) => {
            var userLogin = await AsyncStorage.getItem("userLogin");
            userLogin = JSON.parse(userLogin);
            try {
                let userName = userLogin.UserName;

                var res1 = await GetApis('KPI', 'GetInterActiveUser', { loginname: userName, }, 3000);
                if (res1.ResponseStatus == "OK") {
                    if (res1.ResponseData != null) {
                        let temp = res1.ResponseData.map(item => ({ name: `${item.FullName}`, id: item.UserName }))
                        setLstInterEmp(temp);
                        setPerformUser(userLogin.UserName);
                        setPerformUserTxt(`${userLogin.FullName}`);

                    }
                }

                var res = await GetApis('KPI', 'GetAllEmployees', { username: userName, }, 3000);
                if (res.ResponseStatus == "OK") {
                    if (res.ResponseData != null) {
                        let temp = res.ResponseData.map(item => ({ name: `${item.FullName} - ${item.UserName}`, id: item.UserName }))
                        setLstEmp(temp);
                        let manager = res.ResponseData.find(e => e.res_id == userLogin.repto_id);
                        setManagerUser(manager.UserName);
                        setManagerUserTxt(`${manager.FullName} - ${manager.UserName}`);
                    }
                }

            }
            catch (error) {
                console.log(error);
            }
        }
        if (isFocused) {
            onClearForm();
            getListUser();
        }
    }, [props, isFocused])
    return (
        <View style={[SHCss.container]}>
            <View style={styles.viewhead1}></View>
            <View style={styles.viewhead}>
                <View style={{ flex: 1, justifyContent: 'center' }}>
                    <TouchableOpacity onPress={() => { closeAddTask(navigation) }}>
                        <Text style={styles.headertitle}>Hủy</Text>
                    </TouchableOpacity>
                </View>

                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                    <Text style={{
                        color: theme.COLORS.WHITE,
                        fontWeight: 'bold',
                        fontSize: 18
                    }}>Tạo việc</Text>
                </View>

                <View style={{ flex: 1, alignItems: 'flex-end', justifyContent: 'center' }}>
                    <TouchableOpacity onPress={onSaveTask}>
                        <Text style={styles.headertitle}>Lưu</Text>
                    </TouchableOpacity>
                </View>

            </View>
            <ScrollView>
                <View style={styles.viewPanel}>
                    <SHLabel labelname="Tên công việc *" />
                    <SHInputText
                        placeholder=""
                        name="taskName"
                        value={taskName}
                        onChangeText={(value) => setTaskName(value)} />

                    <SHLabel labelname="Yêu cầu *" />
                    <SHTextArea
                        placeholder=""
                        name="taskRequirement"
                        value={taskRequirement}
                        onChangeText={(value) => setTaskRequirement(value)}
                    />

                    <View style={{ marginTop: 8 }}></View>
                    <View style={styles.containerdate}>
                        <View style={{ flex: 1, marginRight: 4 }}>
                            <SHLabel labelname="Từ ngày *" />
                            <SHModelPicker strDate={strFDate} onPress={() => { showDatePicker('fdate') }} />
                        </View>
                        <View style={{ flex: 1 }}>
                            <SHLabel labelname="Đến ngày *" />
                            <SHModelPicker strDate={strTDate} onPress={() => { showDatePicker('tdate') }} />
                        </View>
                    </View>

                    <View style={{ marginTop: 8 }}></View>
                    <SHLabel labelname="Thực hiện *" />
                    <SHRNPicker
                        placeHolderLabel='Chọn người thực hiện'
                        dataSource={lstInterEmp}
                        dummyDataSource={lstInterEmp}
                        defaultValue={performUser}
                        selectedLabel={performUserTxt}
                        selectedValue={(index, item) => {
                            setPerformUser(item.id), setPerformUserTxt(item.name)
                        }}
                    />

                    <View style={{ marginTop: 8 }}></View>
                    <SHLabel labelname="Đánh giá *" />
                    <SHRNPicker
                        placeHolderLabel='Chọn người đánh giá'
                        dataSource={lstEmp}
                        dummyDataSource={lstEmp}
                        selectedLabel={managerUserTxt}
                        selectedValue={(index, item) => {
                            setManagerUser(item.id), setManagerUserTxt(item.name)
                        }}
                    />

                    <View style={{ marginTop: 8 }}></View>
                    <SHLabel labelname="Theo dõi" />
                    <SHRNPicker
                        placeHolderLabel='Chọn người theo dõi'
                        dataSource={lstEmp}
                        dummyDataSource={lstEmp}
                        selectedLabel={followUserTxt}
                        selectedValue={(index, item) => {
                            setFollowUser(item.id), setFollowUserTxt(item.name)
                        }}
                        multiple={true}
                    />

                    <DateTimePickerModal
                        isVisible={isDatePickerVisible}
                        mode="date"
                        onConfirm={handleConfirm}
                        onCancel={hideDatePicker}
                    />
                </View>
            </ScrollView>
            <Dialog.Container visible={visibleMsgSuccess}>
                <Dialog.Title>Tạo việc thành công!</Dialog.Title>
                <Dialog.Description>
                    Bạn có muốn tiếp tục?
                </Dialog.Description>
                <Dialog.Button color='#ff9933' label="Trang chủ" onPress={() => {
                    setVisibleMsgSuccess(false),
                        navigation.navigate("HomeTab");
                }} />
                <Dialog.Button label="Tiếp tục" onPress={() => {
                    setVisibleMsgSuccess(false)
                        , onClearForm()
                }} />
            </Dialog.Container>
        </View >

    )
}

const AddTask = () => {
    return (
        <Stack.Navigator initialRouteName="AddTask">
            <Stack.Screen
                name="AddTaskComponent"
                component={AddTaskComponent}
                options={{
                    headerShown: false
                }}
            />
        </Stack.Navigator>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: "column",
    },
    containerdate: {
        flexDirection: "row",
    },
    viewhead1: {
        height: 30,
        width: Dimensions.get('window').width,
        backgroundColor: Theme.COLORS.GREEN_PORTAL,
    },
    viewhead: {
        flexDirection: 'row',
        justifyContent: 'center',
        ...Platform.select({
            android: {
                height: 50,
            },
            ios: {
                height: 35,
                paddingBottom: 10
            }
        }),
        width: Dimensions.get('window').width,
        backgroundColor: Theme.COLORS.GREEN_PORTAL,
        paddingRight: 8,
        paddingLeft: 8,
    },
    image: {
        flex: 1,
        flexDirection: 'column',
        resizeMode: "cover",
    },
    text: {
        color: "white",
        fontSize: 42,
        fontWeight: "bold",
        textAlign: "center",
        backgroundColor: "#000000a0"
    },
    viewPanel: {
        padding: 8
    },
    marginBottom4: {
        marginBottom: 4
    },
    headertitle: {
        color: theme.COLORS.WHITE,
        fontWeight: 'bold',
        fontSize: 18,
    }
})

export default AddTask;
