import React from 'react';
import {
    View,
    Text,
    TouchableOpacity,
    Dimensions,
    StyleSheet,
    StatusBar,
    Image,
} from 'react-native';
import * as Animatable from 'react-native-animatable';
import { MaterialIcons } from '@expo/vector-icons';
import { useTheme } from '@react-navigation/native';
import { LinearGradient } from 'expo-linear-gradient';
import { Images } from '../constants';
import ViewSlider from 'react-native-view-slider';
import Theme from '../constants/Theme';
import AsyncStorage from '@react-native-async-storage/async-storage';

const SplashScreen = ({ navigation }) => {
    const { colors } = useTheme();

    const onStart = async () => {
        AsyncStorage.getItem(
            "userLogin"
        ).then(user => {
            if (user) user = JSON.parse(user);
            if (user && user != undefined && user != null) {
                navigation.navigate("App");
            } else {
                navigation.navigate('SignInScreen')
            }
        });
    }

    return (
        <View style={styles.container}>
            {/* <StatusBar backgroundColor='#009387' barStyle="light-content"/> */}
            <View style={styles.header}>
                <ViewSlider
                    renderSlides={
                        <>
                            <View style={styles.viewBox}>
                                <Image source={Images.banner2} style={styles.imageslide} />
                            </View>
                            <View style={styles.viewBox}>
                                <Image source={Images.banner3} style={styles.imageslide} />
                            </View>
                            <View style={styles.viewBox}>
                                <Image source={Images.banner1} style={styles.imageslide} />
                            </View>
                        </>
                    }
                    style={styles.slider}     //Main slider container style
                    slideCount={3}    //How many views you are adding to slide
                    dots={true}     // Pagination dots visibility true for visibile 
                    dotActiveColor='red'     //Pagination dot active color
                    dotInactiveColor='gray'    // Pagination do inactive color
                    dotsContainerStyle={styles.dotContainer}     // Container style of the pagination dots
                    autoSlide={false}    //The views will slide automatically
                    slideInterval={3000}    //In Miliseconds
                />
                {/* <Animatable.Image 
                    animation="bounceIn"
                    duraton="1500"
                    source={Images.logologinshg}
                    style={styles.logo}
                    resizeMode="stretch"
                    /> */}
            </View>
            <Animatable.View
                style={[styles.footer, {
                    backgroundColor: colors.background
                }]}
                animation="fadeInUpBig"
            >
                <Text style={[styles.title, {
                    color: colors.text
                }]}>SUNHOUSE PORTAL</Text>
                <View style={styles.button}>
                    <TouchableOpacity onPress={onStart}>
                        <LinearGradient
                            colors={['#08d4c4', '#01ab9d']}
                            style={styles.signIn}
                        >
                            <Text style={styles.textSign}>Bắt đầu</Text>
                            <MaterialIcons
                                name="navigate-next"
                                color="#fff"
                                size={20}
                            />
                        </LinearGradient>
                    </TouchableOpacity>
                </View>
            </Animatable.View>
        </View>
    );
};

export default SplashScreen;

const { height } = Dimensions.get("screen");
const height_logo = height * 0.28;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#009387'
    },
    header: {
        flex: 2,
        alignItems: 'center'
    },
    footer: {
        flex: 1,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingVertical: 50,
        paddingHorizontal: 30
    },
    logo: {
        width: height_logo,
        height: height_logo
    },
    title: {
        color: '#05375a',
        fontSize: 30,
        fontWeight: 'bold'
    },
    text: {
        color: 'grey',
        marginTop: 5
    },
    button: {
        alignItems: 'flex-end',
        marginTop: 30
    },
    signIn: {
        width: 150,
        height: 40,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 50,
        flexDirection: 'row'
    },
    textSign: {
        color: 'white',
        fontWeight: 'bold'
    },
    viewBox: {
        justifyContent: 'center',
        width: Dimensions.get('window').width,
        padding: 10,
        alignItems: 'center',
        height: 300
    },
    slider: {
        position: 'absolute',
        bottom: 50,
        alignSelf: 'flex-end',
        alignItems: 'center',
        backgroundColor: Theme.COLORS.GREEN_PORTAL
    },
    dotContainer: {
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
    },
    imageslide: {
        height: 280,
        width: Dimensions.get('window').width
    }
});
